import{loadPageContent,load404,hideLoader}from"./pages.53b77682.js";import{loadDetailsPage}from"./components/details.7fb7fe83.js";const routes={"/":"home","/search":"search","/meet-the-creator":"portfolio"};export const handleRoute=()=>{setTimeout(()=>{window.scrollTo(0,0)},10),hideLoader();const o=window.location.hash.substring(1)||"/",[e]=o.split("?"),t=routes[e];if(t)console.log(`Navigating to: ${t}`),loadPageContent(t);else if(o.startsWith("/details-")){const e=o.split("-")[1];console.log(`Navigating to details for: ${e}`),loadDetailsPage(e)}else load404(o)};window.addEventListener("hashchange",handleRoute),window.addEventListener("load",handleRoute);